:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:417:"Earlier this week we welcomed the newest member of our rapidly growing team at WebDevStudios, Brian Layman! Brian is joining WDS as our Senior Programmer Analyst who will be assisting us with project management, custom WordPress development, and server systems administration. &#8230; <a href="http://webdevstudios.com/2011/03/30/wds-welcomes-brian-layman/">Continue reading <span class="meta-nav">&#8594;</span></a>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:12:"April Heline";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:1509:"<p><a href="http://webdevstudios.com/team/brian-layman/"><img src="http://webdevstudios.com/wp-content/uploads/2011/03/BrianLayman2010bio2-300x278.jpg" alt="Brian Layman Photo" width="250" class="alignleft size-medium wp-image-5825" /></a>Earlier this week we <a href="http://twitter.com/#!/brianlayman/status/49904532947353600">welcomed</a> the newest member of our rapidly growing team at WebDevStudios, Brian Layman!  Brian is joining WDS as our Senior Programmer Analyst who will be assisting us with project management, custom WordPress development, and server systems administration.  He has expertise in PHP, JavaScript, MySQL development, database management and server administration and is a featured WordPress Developer in <span style="text-decoration: underline;">WordPress for Dummies (2nd edition)</span> by Lisa Sabin-Wilson.</p>\n<p>Before joining WDS Brian founded eHermits, Inc. and has worked for such notable companies as b5media, Borland International, RedRover, Rhett and Link Inc. and Darren “Problogger” Rowse.  He is very well known and respected in the WordPress community with publicly released plugins, WordPress Core contributions, and is always quick to chime in on a WP Hackers mailing list conversation.  Brian brings a wealth of WordPress knowledge and experience to our team and we are very excited to have him on board!</p>\n<p>Find more information about Brian on his WDS <a title="Brian Layman" href="http://webdevstudios.com/team/brian-layman/">Team Page</a>.</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:67:"http://webdevstudios.com/2011/03/30/wds-welcomes-brian-layman/feed/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"3";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}}}s:44:"http://purl.org/rss/1.0/modules/syndication/";a:2:{s:12:"updatePeriod";a:1:{i:0;a:5:{s:4:"data";s:6:"hourly";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:15:"updateFrequency";a:1:{i:0;a:5:{s:4:"data";s:1:"1";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:27:"http://www.w3.org/2005/Atom";a:1:{s:4:"link";a:2:{i:0;a:5:{s:4:"data";s:0:"";s:7:"attribs";a:1:{s:0:"";a:3:{s:3:"rel";s:4:"self";s:4:"type";s:19:"application/rss+xml";s:4:"href";s:41:"http://feeds.feedburner.com/webdevstudios";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:0:"";s:7:"attribs";a:1:{s:0:"";a:2:{s:3:"rel";s:3:"hub";s:4:"href";s:32:"http://pubsubhubbub.appspot.com/";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:42:"http://rssnamespace.org/feedburner/ext/1.0";a:3:{s:4:"info";a:1:{i:0;